        Roll_now_degree=Roll_now_degree+theta_P(4,1);
        Pitch_now_degree=Pitch_now_degree+theta_P(5,1);                          
        Yaw_now_degree=Yaw_now_degree+theta_P(6,1);

    else
        break;
    end
end

